$a = Get-Date -format F
$smtp = "172.23.131.4" 
$to = "dl-EUM-ITSupport@valaris.com" 
$from = "DHCP_Server_Status_Report@valaris.com"  
$subject = "EUM DHCP Server Status Report."  
$body = "EUM DHCP Server status report for review.<br>" 
$body += "Please review and check any unknown DHCP leases."
$OutFile = "C:\Script Reports\DHCP\EUM_DHCP_Server_Status_Report.html"

$Header = @"
<head>
    <title>EUM DHCP Server Status</title>
</head>
<style>
TABLE {width: 90%;margin: auto;border-width: 1px;border-style: solid;border-color: black;border-collapse: collapse;}
TH {text-align: center;border-width: 1px;padding: 3px;border-style: solid;border-color: black;background-color: #6495ED;}
TD {text-align: center;border-width: 1px;padding: 3px;border-style: solid;border-color: black;min-width:150px;max-width:150px}
TR:Nth-Child(Even) {Background-Color: #dddddd;}
TR:Hover TD {Background-Color: #C1D5F8;}
</style>
</script>
<br>
<img src=data:image/png;base64, alt="Ensco Logo" style='float:Left'/>
<img src=data:image/png;base64, style='float:Right'/>
<h1 align="center">EUM DHCP Server Status Report</H1>
<br><br>
"@
$Pre = " EUM DHCP Server Status report"
$Post =  " <br><b>Report created $a<b> "
$out_body = " "

$hosts = Get-Content "C:\Script Reports\DHCP\serverlisteum.txt"

foreach ($h in $hosts)
{
    #echo $h
    $out_body += "<h2>" + $h + "</h2>"
    $out_body += Invoke-Command -ScriptBlock { 
        Get-DhcpServerv4Scope -ComputerName $h | Get-DhcpServerv4Lease -ComputerName $h | select IPAddress, AddressState, ClientId, HostName, LeaseExpiryTime, ServerIP | ConvertTo-HTML | Format-Table -HideTableHeaders | Out-String } 
}

# Convert to HTML Page
ConvertTo-HTML -Head $Header -PostContent $Post -Body $out_body | Out-File $OutFile

# Send e-mail
Send-MailMessage -SmtpServer $smtp -To $to -From $from -Subject $subject -Body $body -BodyAsHtml -Attachments $OutFile

#Remove File
Get-Item $OutFile | Remove-Item -Force