#requires -version 2.0 
#use parameter  drive report to html.ps1 computer1,computer2 or a computer list file 
#change file path and name on line 7 below to reflect name and  path of computer list file using. 
#script will open web browser with current report when completed. 
 
Param ( 
$computers = (Get-Content  "C:\Script Reports\Drive Space Report\SLEUM.TXT") 
) 
 
$Title="Hard Drive Report to HTML" 
 
#embed a stylesheet in the html header min-width:50px;max-width:200px
$head = @"  
<style>
TABLE {width: 90%;margin: auto;border-width: 1px;border-style: solid;border-color: black;border-collapse: collapse;}
TH {text-align: center;border-width: 1px;padding: 3px;border-style: solid;border-color: black;background-color: #6495ED;}
TD {text-align: center;border-width: 1px;padding: 3px;border-style: solid;border-color: black;min-width:150px;}
#TR:Nth-Child(Even) {Background-Color: #dddddd;}
TR:Hover TD {Background-Color: #FFFFFF;}
</style>
<br>
<img src=data:image/png;base64, alt="Drive Logo" style='float:Right'/>
<img src=data:image/png;base64, alt="Ensco Logo" style='float:Left'/>
<h1 align="center">EUM Drive Space Status Report</H1>
<br><br>
<Title>$Title</Title> 
<br> 
"@  
 
$OutFile = "C:\Script Reports\Drive Space Report\EUM Offshore Hard Drive Report.html"
$smtp = "smtp.drillzone.com" 
$to = "dl-EUM-ITSupport@valaris.com" 
$from = "Drive_Status_Report@valaris.com"  
$subject = "EUM Offshore Hard Drive Space Status Report."  
$body = "EUM offshore Drive Space status report for review.<br>"
$a = Get-Date -format F
$Post =  " <br><b>Report created $a<b> "

#define an array for html fragments 
$fragments=@() 
 
#get the drive data 
$Data=ForEach ($computer in $computers){Invoke-command -ComputerName $computer -ScriptBlock {Get-WmiObject -Class Win32_logicaldisk -filter "drivetype=3"}}
 
#group data by computername 
$groups=$Data | Group-Object -Property SystemName 
 
#this is the graph character 
[string]$g=[char]9608  
 
#create html fragments for each computer 
#iterate through each group object 
         
ForEach ($computer in $groups) { 

    $fragments+="<H2>$($computer.Name)</H2>" 
     
    #define a collection of drives from the group object 
    $Drives=$computer.group 
    echo $computer 
    #create an html fragment 
    $html=$drives | Select @{Name="Drive Letter";Expression={$_.DeviceID}}, 
    @{Name="Size GB";Expression={$_.Size/1GB  -as [int]}}, 
    @{Name="Used GB";Expression={"{0:N2}" -f (($_.Size - $_.Freespace)/1GB) }}, 
    @{Name="Free GB";Expression={"{0:N2}" -f ($_.FreeSpace/1GB) }},
    @{Name="Percentage Used";Expression={
      $Percent= (($_.Size - $_.Freespace)/$_.Size)*100 
      "{0:N2}" -f $Percent + '%'
      
    }},  
    @{Name="Usage Chart";Expression={ 
      $UsedPer= (($_.Size - $_.Freespace)/$_.Size)*100 
      $UsedGraph=$g * ($UsedPer/2) 
      $FreeGraph=$g* ((100-$UsedPer)/2) 

 #sets the percentage to change the barchart to red for warning
    [decimal]$threshold = 90

    #decides whether to make the chart red or green
    If ($UsedPer -gt $threshold){$colour ='red'}
    Else {$colour = '#04B45F'}
      #I'm using place holders for the < and > characters 
      "xopenFont color=$colour xclose{0}xopen/FontxclosexopenFont Color=#D8D8D8xclose{1}xopen/fontxclose" -f $usedGraph,$FreeGraph
       
    }} | ConvertTo-Html -Fragment  
     
    #replace the tag place holders. It is a hack but it works. 
    $html=$html -replace "xopen","<" 
    $html=$html -replace "xclose",">" 
     
    #add to fragments 
    $Fragments+=$html 
     
    #insert a return between each computer 
    $fragments+="<br>" 
     
} #foreach computer 
 
#add a footer 
#$footer=("<br><I>Report run {0} by {1}\{2}<I>" -f (Get-Date -displayhint date),$env:userdomain,$env:username) 
#$fragments+=$footer 
 
#write the result to a file 
ConvertTo-Html -head $head -PostContent $Post -body $fragments  | Out-File $OutFile

# Send e-mail
Send-MailMessage -SmtpServer $smtp -To $to -From $from -Subject $subject -Body $body -BodyAsHtml -Attachments $OutFile

#Remove File
Get-Item $OutFile | Remove-Item -Force
