$a = Get-Date -format F
$smtp = "smtp.drillzone.com" 
$to = "dl-EUM-ITSupport@valaris.com" 
$from = "VM_Replication_Status_Report@valaris.com"  
$subject = "EUM VM Replication Status Report."  
$body = "EUM VM replication status report for review.<br>" 
$body += "Please review and rectify ReplicationHealth with a status of<font color=red> Critical</font> or Warning."
$OutFile = "C:\Script Reports\Replication Status\EUM_VM_Replication_Status_report.html"

$Header = @"
<head>
    <title>EUM Hyper-V Replication Status</title>
</head>
<style>
TABLE {width: 90%;margin: auto;border-width: 1px;border-style: solid;border-color: black;border-collapse: collapse;}
TH {text-align: center;border-width: 1px;padding: 3px;border-style: solid;border-color: black;background-color: #6495ED;}
TD {text-align: center;border-width: 1px;padding: 3px;border-style: solid;border-color: black;min-width:150px;max-width:150px}
TR:Nth-Child(Even) {Background-Color: #dddddd;}
TR:Hover TD {Background-Color: #C1D5F8;}
</style>
</script>
<br>
<img src=data:image/png;base64, alt="Ensco Logo" style='float:Left'/>
<img src=data:image/png;base64, alt="Microsoft Hyper-V" style='float:Right'/>
<h1 align="center">EUM VM Replication Status Report</H1>
<br><br>
"@
$Pre = " EUM VM Replication Status report"
$Post =  " <br><b>Report created $a<b> "
$out_body = " "

$hosts = Get-Content "C:\Script Reports\Replication Status\serverlisteum.txt"

foreach ($h in $hosts)
{
    gpupdate /force
    #echo $h
    $out_body += "<h2>" + $h + "</h2>"
    $out_body += Invoke-Command -ComputerName $h -ScriptBlock { 
        get-VMReplication | select vmname, primaryserver, replicaserver, replicationmode, State, replicationhealth, @{Expression={"{0:N0}" -f ((get-date)-($_.lastreplicationtime)).TotalHours};Label="Hours since last replication (hrs)"} | 
        ConvertTo-HTML | Format-Table -HideTableHeaders | Out-String | ForEach-Object { $_ -replace "Critical","<font color='FF0000'>Critical</font>" } } 
}

# Convert to HTML Page
ConvertTo-HTML -Head $Header -PostContent $Post -Body $out_body | Out-File $OutFile

# Send e-mail
Send-MailMessage -SmtpServer $smtp -To $to -From $from -Subject $subject -Body $body -BodyAsHtml -Attachments $OutFile

#Remove File
Get-Item $OutFile | Remove-Item -Force